const App = () => {

  const categories = [
    {
      id: 1,
      title: 'Hats'
    },
    {
      id: 2,
      title: 'Shoes'
    }
  ]

  return (
    <div className="categories-container">
      {categories.map(({title, id}) => (
        <div className="category-container" key={id} >
          {/* <img /> */}
          <div className="category-body-container">
            <h2>{title}</h2>
            <h4>Shop now!</h4>
          </div>
        </div>
      ))}
    </div>
  );
}

export default App;
